import React, { Component } from "react";
import AddUser from "./components/AddUser";
import Users from "./components/Users";

class App extends Component{


  constructor(props){
    super(props);
    this.state = {
      users: [
        {
          id: 1,
          name: "Kayahan Sanli",
          email:"kayahansan24@gmail.com"
        },
        {
          id: 2,
          name: "Aslıhan Sanli",
          email:"aslihanbalata@gmail.com"
        },
        {
          id: 3,
          name: "Yavuz Selim Arslan",
          email:"yavuzselim@gmail.com"
        }
      ]

    };
    this.deleteUsers = this.deleteUsers.bind(this);
  }
  deleteUsers(id){
    const updatedUsers = this.state.users;
    updatedUsers= updatedUsers.filter(user =>user.id !== id);
    this.setState({
      users: updatedUsers
    });
  }
  render() {
    return(
      <div className="container">
        <h4>User App</h4>
        <hr />     
        <AddUser/>
        <Users deleteUsers={this.state.deleteUsers} users = {this.state.users}/>
      </div>

    );
  }
}
export default App;
